INSERT INTO `products` (`id`, `name`, `cate_id`, `price`, `discount`, `image`, `view`, `quantity`, `detail`, `hot`, `status`, `created_at`, `updated_at`) VALUES
(31, 'Iphone 13 Pro Max', 1, 27990000, 10, 'iphone-13-mini-pink-1-600x600.jpg', 57, 100, 'iPhone 13 Pro Max xứng đáng là một chiếc iPhone lớn nhất, mạnh mẽ nhất và có thời lượng pin dài nhất từ trước đến nay sẽ cho bạn trải nghiệm tuyệt vời, từ những tác vụ bình thường cho đến các ứng dụng chuyên nghiệp.\r\nĐắm chìm trong không gian màn hình lớn cực đã\r\nDù là giải trí khi xem phim, chơi game hay kiểm tra email, đọc tài liệu thì màn hình lớn tới 6,7 inch của iPhone 13 Pro Max cũng luôn cho trải nghiệm tuyệt vời nhất.\r\n\r\nKhông chỉ lớn, đây còn là màn hình chất lượng hàng đầu thế giới smartphone với tấm nền OLED tuyệt đẹp, công nghệ Super Retina XDR siêu nét và độ sáng tối đa đạt mức khó tin, lên tới 1200 nits cho nội dung HDR. Trước mắt bạn là một không gian giải trí mãn nhãn, một thiết bị di động lý tưởng để giải quyết nhanh công việc với màn hình thực sự xuất sắc.', 1, 0, '2022-11-21 07:06:55', '2022-12-26 19:54:55'),
(34, 'Iphone 13', 1, 19490000, 5, 'iphone-13-starlight-1-600x600.jpg', 3, 100, 'iPhone 13 sở hữu hệ thống camera kép xuất sắc nhất từ trước đến nay, bộ vi xử lý Apple A15 nhanh nhất thế giới smartphone cùng thời lượng pin cực khủng, sẵn sàng đồng hành cùng bạn suốt cả ngày.\r\nThiết kế sang trọng và bền bỉ\r\n\r\niPhone 13 là chiếc điện thoại toát lên sự đẳng cấp ngay từ cái nhìn đầu tiên nhờ thiết kế sang trọng với ngôn ngữ thiết kế phẳng và chất lượng hoàn thiện tuyệt vời.\r\n\r\nKhung nhôm cao cấp kết hợp cùng kính bảo vệ Ceramic Shield siêu cứng tạo nên chiếc điện thoại vừa thời trang, lại vô cùng bền bỉ. Khả năng chống nước chuẩn IP68 giúp bạn hoàn toàn yên tâm trước mọi nguy cơ từ nước trong quá trình sử dụng thường ngày.\r\n\r\niPhone 13 màu xanh lá độc đáo\r\nSang trọng hơn và độc đáo hơn, phiên bản màu xanh lá hoàn toàn mới đã góp mặt trên iPhone 13 tiêu chuẩn. Sự trầm lặng, nét cao cấp và vẻ ngoài bóng bẩy của chiếc điện thoại như được tôn vinh và mang tới cảm nhận hoàn toàn khác biệt về cấu trúc thẩm mỹ.', 0, 0, '2022-11-21 07:34:53', '2022-12-25 23:07:26'),
(35, 'Iphone 12', 1, 16499000, 5, 'iphone-12-den-new-2-600x600.jpg', 2, 300, 'iPhone 12 ra mắt với vai trò mở ra một kỷ nguyên hoàn toàn mới. Tốc độ mạng 5G siêu tốc, bộ vi xử lý A14 Bionic nhanh nhất thế giới smartphone, màn hình OLED tràn cạnh tuyệt đẹp và camera siêu chụp đêm, tất cả đều có mặt trên iPhone 12.\r\n\r\nThiết kế mỏng nhẹ, nhỏ gọn và đẳng cấp\r\niPhone 12 đã có sự đột phá về thiết kế với kiểu dáng mới vuông vắn, mạnh mẽ và sang trọng hơn. Không chỉ vậy, iPhone 12 mỏng hơn 11%, nhỏ gọn hơn 15% và nhẹ hơn 16% so với thế hệ trước iPhone 11.\r\n\r\nBất ngờ hơn nữa là dù gọn hơn đáng kể nhưng iPhone 12 vẫn có màn hình 6,1 inch như iPhone 11 mà không hề bị cắt giảm. Phần viền màn hình thu hẹp tối đa cùng sự nỗ lực trong thiết kế đã tạo nên điều kỳ diệu ở iPhone 12.', 0, 0, '2022-11-21 07:38:36', '2022-12-25 21:52:20'),
(39, 'SAMSUNG GALAXY S22 ULTRA', 8, 25990000, 10, 'Galaxy-S22-Ultra-Burgundy-600x600.jpg', 7, 100, 'Thiết kế đẳng cấp sang trọng\r\nLấy cảm hứng thiết kế từ những dòng Galaxy Note trước đây, với các góc vuông cùng viền màn hình được thiết kế mỏng, mang đến không gian hiển thị rộng lớn cho những trải nghiệm giải trí hay thao tác công việc trên máy.\r\nMặt lưng được thiết kế từ kính nhám giúp thiết bị tăng vẻ sang trọng cũng như hạn chế bám dấu vân tay, mồ hôi trong quá trình sử dụng hàng ngày.\r\n\r\nCụm camera giờ đây không còn bố trí trong cụm mà được đặt trần trên mặt lưng nhằm mang đến vẻ mới lạ cũng như đặc điểm nhận dạng thiết bị, giúp gây sự chú ý đối với mọi người xung quanh khi cầm nắm sử dụng trên tay.\r\n\r\nViền màn hình được bo cong nhẹ giúp hạn chế tình trạng cấn tay, mang đến cảm giác cầm nắm khá êm khi dùng máy liên tục trong một khoảng thời gian dài.\r\n\r\nMàn hình lớn tràn viền\r\nMáy sử dụng tấm nền Dynamic AMOLED 2X với kích thước lên đến 6.8 inch giúp hiển thị nội dung một cách đầy đủ, với viền màn hình được tối ưu tốt mang đến cảm giác sử dụng cực kỳ thoải mái khi sử dụng.\r\n\r\nXem thêm: Tìm hiểu màn hình Dynamic AMOLED 2X trên smartphone\r\n\r\nĐộ phân giải màn hình đạt tiêu chuẩn 2K+ giúp hình ảnh được hiển thị một cách chi tiết, hỗ trợ tốt trong các tác vụ chỉnh sửa hình ảnh, video trên thiết bị.', 1, 0, '2022-11-21 08:16:42', '2022-12-26 19:45:02'),
(48, 'Samsung Galaxy Z Fold3', 8, 28989800, 10, 'samsung-galaxy-z-fold-3-silver-1-600x600.jpg', 2, 200, 'Thiết kế mang tầm thương hiệu\r\nGalaxy Z Fold3 5G sở hữu ngoại hình tương tự thế hệ trước với màn hình gập vào trong bản lề ở giữa. Tuy nhiên, với cải tiến thiết kế thì lần này thiết bị khi gập lại đã trở nên mỏng hơn nhiều.\r\n\r\nSamsung Galaxy Z Flod3 5G | Thiết kế mang tầm thương hiệu\r\n\r\nVới cơ chế gập mở linh hoạt ở nhiều góc độ khác nhau bạn gần như có một chiếc tablet mini hay một chiếc smartphone bình thường một cách dễ dàng.\r\n\r\nSamsung Galaxy Z Flod3 5G | Màn hình có thể gập lại dưới nhiều góc độ khác nhau\r\n\r\nGalaxy Z Fold3 5G được chế tác từ những vật liệu cao cấp với khung viền hợp kim nhôm Armor Aluminum giúp cho thiết bị bền bỉ hơn 10% so với các vật liệu trước đây. Giờ đây bạn sẽ yên tâm hơn khi tận hưởng các hoạt động yêu thích cách trọn vẹn nhất.\r\n\r\nSamsung Galaxy Z Flod3 5G | Khung viền nhôm Armor Aluminum cao cấp\r\n\r\nMàn hình ngoài sử dụng kính cường lực Gorilla Glass Victus rất cứng cáp, đảm bảo cho máy được an toàn khi vô tình đánh rơi ở khoảng cách 2 m cũng như chống trầy xước đến 4 lần cho bạn thoải mái sử dụng.', 0, 0, '2022-11-21 08:35:20', '2022-12-13 09:53:43'),
(52, 'Samsung Galaxy Z Fold4', 8, 37990000, 20, 'samsung-galaxy-z-fold4-kem-256gb-600x600.jpg', 6, 200, 'Samsung Galaxy Z Fold4 256GB sẽ có thiết kế khá giống với một chiếc máy tính bảng, máy sử dụng cơ chế gập hiện đại.\r\n\r\nĐộ bền của điện thoại đã được kiểm chứng qua nhiều bài đánh giá khắt khe khác nhau, cho nên Samsung tự tin cho biết Galaxy Z Fold4 có tuổi thọ sử dụng và số lần gập gia tăng rất nhiều so với thế hệ tiền nhiệm.\r\n\r\nBền bỉ cực cao - Samsung Galaxy Z Fold4 256GB\r\n\r\nBộ khung của máy và phần bản lề sẽ được làm từ nhôm Armor Aluminum cao cấp, vừa có trọng lượng nhẹ và vừa có độ bền bỉ cao.\r\n\r\nKhung và bản lè chắc chắn - Samsung Galaxy Z Fold4 256GB\r\n\r\nVới những bạn có sở thích ghi chép và phải làm việc với tính chất đặc thù là ghi chép nhanh hay phác họa, Galaxy Z Fold4 256GB có lẽ là một lựa chọn tối ưu dành cho các bạn bởi máy sẽ có chức năng tương tự như một cuốn sổ truyền thống khi sử dụng kèm với bút S Pen.\r\n\r\nĐiểm mà hãng có nhấn mạnh trên dòng sản phẩm lần này chuẩn kháng nước IPX8 cao cấp, đây là công nghệ chưa từng được trang bị trên bất kỳ dòng điện thoại gập nào thì nay đã được giới thiệu trên Galaxy Z Fold4.', 1, 0, '2022-11-21 08:42:17', '2022-12-25 22:46:05'),
(53, 'Samsung Galaxy S21 FEs', 1, 12990000, 20, 'Samsung-Galaxy-S21-FE-trang-1-2-600x600.jpg', 9, 200, 'Cảm nhận chạm vuốt cực mượt, khung hình rực rỡ sắc nét\r\nSamsung Galaxy S21 FE 5G sở hữu tấm nền Dynamic AMOLED 2X với dải màu cực rộng và độ tương phản cao, độ phân giải chuẩn Full HD+ thể hiện từng chi tiết và màu sắc chuẩn xác, sắc nét đầy chân thực. Độ sáng màn hình tối đa 1200 nits nên khi sử dụng ngoài trời vẫn mang đến chất lượng hiển thị tốt, không bị lóa mờ.\r\n\r\nMàn hình sắc nét - Samsung Galaxy S21 FE\r\n\r\nMàn hình 6.4 inch được phủ lớp kính cường lực Gorilla Glass Victus chống nứt vỡ và trầy xước tốt cho màn hình hiển thị có độ trong suốt cao, đồng thời bền bỉ trong quá trình sử dụng. Camera nốt ruồi và các cạnh viền mỏng cho không gian trải nghiệm màn hình tối ưu hơn. Chuẩn kháng nước và chống bụi IP68 cũng bảo vệ tốt smartphone khi vô tình tiếp xúc với nước.\r\n\r\nTrải nghiệm ấn tượng - Samsung Galaxy S21 FE\r\n\r\nĐặc biệt, trải nghiệm cảm giác chạm, lướt, cuộn màn hình và chuyển cảnh cực mượt nhờ tần số quét lên đến 120 Hz, thao tác không độ trễ, tận dụng từng cơ hội làm chủ trong các trận đấu game gay cấn.', 0, 0, '2022-11-21 08:45:56', '2022-12-27 00:05:16'),
(58, 'Samsung Galaxy A73', 8, 10999000, 20, 'Samsung-Galaxy-A73-PUBG-Thumb-600x600.jpg', 6, 200, 'Bắt trọn khoảnh khắc một cách sắc nét\r\nNổi bật nhất trên chiếc máy này đó chính là khả năng chụp ảnh chất lượng cao, với 4 camera được trang bị ở mặt lưng có thông số: Camera chính 108 MP, camera góc siêu rộng 12 MP, cảm biến độ sâu và macro có cùng độ phân giải 5 MP, cho phép mình thỏa sức chụp ảnh với nhiều tính năng.\r\n\r\nTrang bị 4 camera - Samsung Galaxy A73 5G 128GB\r\n\r\nMình có sử dụng qua Galaxy A73 ở chế độ chụp ảnh trong môi trường đủ sáng, chất lượng ảnh mà máy thu lại có độ sắc nét cực cao, quang cảnh bắt mắt và rực rỡ, chất lượng ảnh không bị thay đổi quá nhiều so với độ nét ban đầu sau khi hậu kỳ.\r\n\r\nẢnh chụp sắc nét - Samsung Galaxy A73 5G 128GB\r\n\r\nVề khả năng thu sáng khi chụp ảnh ban đêm thì Galaxy A73 làm rất tốt, tổng quan bức ảnh trông khá rõ ràng, những vùng tối được AI xử lý và làm sáng nhẹ nhằm giúp cho bức ảnh trông hài hòa và đẹp mắt hơn.\r\n\r\nẢnh chụp đêm - Samsung Galaxy A73 5G 128GB\r\n\r\nĐối với bức ảnh chụp xóa phông thì không có gì để chê, bới kết quả mang lại làm cho mình hơi bị bất ngờ khi máy xử lý xóa phông nhìn rất mịn, tình trạng lẹm vào chủ thể gần như không xuất hiện kể cả chụp vào ban đêm.', 0, 0, '2022-11-21 08:55:18', '2022-12-14 07:24:39'),
(62, 'Samsung Galaxy S21 FE', 8, 12988000, 2, 'Samsung-Galaxy-S21-FE-trang-1-2-600x600.jpg', 1, 222, 'Cảm nhận chạm vuốt cực mượt, khung hình rực rỡ sắc nét Samsung Galaxy S21 FE 5G sở hữu tấm nền Dynamic AMOLED 2X với dải màu cực rộng và độ tương phản cao, độ phân giải chuẩn Full HD+ thể hiện từng chi tiết và màu sắc chuẩn xác, sắc nét đầy chân thực. Độ sáng màn hình tối đa 1200 nits nên khi sử dụng ngoài trời vẫn mang đến chất lượng hiển thị tốt, không bị lóa mờ. Màn hình sắc nét - Samsung Galaxy S21 FE Màn hình 6.4 inch được phủ lớp kính cường lực Gorilla Glass Victus chống nứt vỡ và trầy xước tốt cho màn hình hiển thị có độ trong suốt cao, đồng thời bền bỉ trong quá trình sử dụng. Camera nốt ruồi và các cạnh viền mỏng cho không gian trải nghiệm màn hình tối ưu hơn. Chuẩn kháng nước và chống bụi IP68 cũng bảo vệ tốt smartphone khi vô tình tiếp xúc với nước. Trải nghiệm ấn tượng - Samsung Galaxy S21 FE Đặc biệt, trải nghiệm cảm giác chạm, lướt, cuộn màn hình và chuyển cảnh cực mượt nhờ tần số quét lên đến 120 Hz, thao tác không độ trễ, tận dụng từng cơ hội làm chủ trong các trận đấu game gay cấn.', 0, 0, '2022-11-21 09:04:05', '2022-12-25 19:55:07'),
(64, 'Iphone 14 Pro', 1, 22799000, 1, '202211220529iphone-14-pro-tim-thumb-600x600.jpg', 1, 200, 'Vẻ ngoài được hoàn thiện cao cấp\r\niPhone 14 128GB lộ diện với ngoại hình đẹp mắt đầy thời thượng, máy thừa hưởng gần như tất cả những gì tinh túy nhất trên phần thiết kế đến từ những thế hệ trước như iPhone 13 Series.\r\nVề phần hoàn thiện thì máy được trang bị những loại vật liệu cực kỳ cao cấp từ mặt kính cường lực cho đến khung nhôm chắc chắn. Không chỉ giúp chiếc máy của bạn toát lên vẻ ngoài sang trọng mà đây còn làm cho thiết bị trở nên chắc chắn và bền bỉ với thời gian.\r\n\r\nTrải nghiệm nội dung đã mắt trên màn hình chất lượng\r\niPhone 14 128GB được trang bị một tấm nền OLED cao cấp có độ phân giải 1170 x 2532 Pixels, mang lại cho thiết bị khả năng tái hiện hình ảnh trong trẻo và độ chính xác về màu sắc cao. Những chi tiết nhỏ cũng sẽ được tái hiện rõ nét bởi mật độ điểm ảnh khá cao.', 1, 0, '2022-11-21 22:29:30', '2022-11-21 22:30:23'),
(65, 'OPPO FIND X5 PRO', 9, 26990000, 5, '202211220613oppo-find-x5-pro-den-thumb-600x600.jpg', 1, 300, 'OPPO Find X5 Pro là một trong những sản phẩm cao cấp được OPPO tung ra thị trường với thiết kế thời thượng, màn hình 2K+ chất lượng cao, cấu hình mạnh mẽ,... hứa hẹn sẽ là một sản phẩm đáp ứng đầy đủ các tiêu chí của người dùng.\r\nMàn hình AMOLED kích thước lớn cho trải nghiệm giải trí ấn tượng\r\n\r\nOPPO trang bị cho điện thoại này màn hình AMOLED kích thước lên đến 6.7 inch cùng độ phân giải 2K+ (1440 x 3216 Pixels) giúp bạn có được trải nghiệm xem đã mắt với chất lượng hình ảnh hiển thị chân thực, sắc nét và màu sắc tái tạo tự nhiên, ngoài ra còn tiết kiệm điện năng đáng kể.\r\n\r\nMàn hình AMOLED trên OPPO Find X5 Pro có độ sâu màu 10 bit kết hợp cùng độ sáng 1300 nits mang lại chất lượng màu sắc rực rỡ hơn, đảm bảo vẫn thể hiện được những nội dung trên điện thoại với chất lượng màu sắc tự nhiên, chính xác trong môi trường nhiều ánh sáng và nắng gắt.\r\n\r\nFind X5 Pro được nâng cao khả năng chống trầy xước, va đập cho màn hình khi được trang bị kính cường lực Corning Gorilla Glass Victus với độ bền theo thời gian.', 1, 0, '2022-11-21 23:13:57', '2022-11-21 23:14:05'),
(66, 'OPPO RENO8', 9, 8690000, 1, '202211220622oppo-reno8-4g-thumb-vang-600x600.jpg', 1, 100, 'Mới đây OPPO đã chính thức tung ra thị trường bộ sản phẩm mới nhất đến từ dòng Reno với nhiều nâng cấp đáng kể, OPPO Reno8 sẽ là một cái tên trong số đó với nhiều nâng cấp đáng giá như hiệu năng cao, camera chụp ảnh sắc nét.\r\nDiện mạo thời trang\r\nNổi bật với ngoại hình thời thượng nhờ cạnh viền cùng mặt lưng được vát phẳng một cách tinh tế. Sử dụng mặt lưng kính và kiểu hoàn thiện nhám giúp máy có được khả năng hạn chế các vết xước hay dấu vân tay trong quá trình sử dụng.\r\n\r\nThiết kế hiện đại - OPPO Reno8\r\n\r\nMàn hình chất lượng\r\nĐiện thoại sử dụng công nghệ màn hình AMOLED với kích thước 6.4 inch cho ra hình ảnh có màu sắc tươi mới cùng không gian hiển thị rộng lớn. Với tần số quét 90 Hz giúp mọi thao tác cuộn, lướt của máy được diễn ra mượt mà.\r\n\r\nMàn hình chất lượng - OPPO Reno8\r\n\r\nNhờ độ sáng tối đa có thể đạt được lên tới 600 nits nên người dùng có thể sử dụng điện thoại Reno ngoài trời hay những môi trường có độ sáng cao dễ dàng hơn.', 0, 0, '2022-11-21 23:22:09', '2022-11-21 23:27:49'),
(67, 'OPPO A95', 9, 5490000, 5, '202211220626oppo-a95-4g-bac-2-600x600.jpg', 0, 100, 'OPPO A95 4G, một chiếc điện thoại tầm trung tạo ấn tượng lớn tới người tiêu dùng bởi thiết kế mỏng nhẹ, sang trọng, màn hình lớn cùng bộ 3 camera chuyên nghiệp phía sau giúp bạn thoải mái làm việc, liên lạc, thư giãn một cách hiệu quả.\r\nThiết kế OPPO Glow sang trọng, sáng bóng\r\nĐiện thoại OPPO A95 4G mang phong cách thiết kế hoàn toàn mới so với người tiền nhiệm khi trang bị họa tiết nhám được hoàn thiện tỉ mỉ và chính xác sẽ tạo nên một sản phẩm thực sự nổi bật mà ai cũng phải ngước nhìn. \r\n\r\nĐiện thoại OPPO A95 4G | Thiết kế OPPO Glow sang trọng, sáng bóng\r\n\r\nToàn thân máy đều làm cong 2.5D mượt mà, với độ mỏng chỉ 7.95 mm sang trọng nổi bật với hiệu ứng chuyển màu bắt mắt khi thay đổi góc nhìn, hạn chế tình trạng bám dấu vân tay cho cảm giác rất dễ chịu trong lúc sử dụng. \r\n\r\nĐiện thoại OPPO A95 4G | Thiết kế mỏng nhẹ\r\n\r\nMàn hình lớn thoải mái giải trí đa chức năng\r\nPhía trước, màn hình OPPO A95 4G được thiết kế tối giản với cụm camera selfie nằm ngay trong màn cho không gian hiển thị tốt rộng rãi lên 6.43 inch để bạn tha hồ tận hưởng những nội dung yêu thích như xem phim, đọc báo, lướt web, vào mạng xã hội,...', 0, 0, '2022-11-21 23:26:51', '2022-11-21 23:26:51'),
(68, 'OPPO A77s', 9, 5900000, 1, '202211220630oppo-a77s-den-thumb-1-2-600x600.jpg', 1, 100, 'OPPO A77s là chiếc điện thoại OPPO dòng A vừa được cho ra mắt trên thị trường vào tháng 10/2022, máy tập trung chủ yếu phần camera và hiệu năng nhằm mang đến những trải nghiệm mượt mà cũng như nâng cấp khả năng chụp ảnh để giúp bạn có được những bức hình chất lượng.\r\n• OPPO A77s sử dụng nguyên lý thiết kế phẳng với tạo hình vuông vức ở các cạnh và hai mặt, điều này mang lại cho thiết bị cái nhìn bắt mắt và cực kỳ hợp với xu hướng hiện nay.\r\n\r\n• Chiếc điện thoại OPPO này được trang bị màn hình lớn với kích thước 6.56 inch và độ phân giải HD+ (720 x 1612 Pixels), nội dung hiển thị có độ chi tiết tương đối tốt cùng một không gian rộng rãi để bạn có thể giải trí thỏa thích.\r\n\r\n• Máy được sử dụng công nghệ màn hình IPS LCD giúp tái hiện hình ảnh có màu sắc chân thực, độ sáng cao và góc nhìn rộng để người dùng có thể dễ dàng sử dụng ở nhiều góc độ khác nhau.\r\n\r\n• Mặt lưng của điện thoại sẽ được trang bị hai camera với cảm biến chính có độ phân giải lên tới 50 MP, đi kèm với đó sẽ là nhiều tính năng hữu ích để bạn có thể chụp được nhiều bức ảnh chất lượng hơn, những tính năng nổi bật có thể kể đến như: Ban đêm, chuyên nghiệp, làm đẹp, xóa phông, siêu độ phân giải,...\r\n\r\n• Chiếc điện thoại Android này được trang bị con chip Snapdragon 680 4G với hiệu suất tối đa đạt được là 2.4 GHz, mang lại khả năng xử lý tác vụ được mượt mà hơn để tối ưu tốt mọi trải nghiệm mà bạn sử dụng.\r\n\r\n• OPPO A77s là điện thoại RAM 8 GB đi cùng khả năng mở rộng RAM lên đến 8 GB nên người dùng có thể an tâm về phần đa nhiệm của máy, hiện tượng tải lại ứng dụng sau một khoảng thời gian dài mở lại cũng đã được giảm đi đáng kể so với những sản phẩm khác cùng dòng nhưng có RAM thấp hơn.', 0, 0, '2022-11-21 23:30:58', '2022-11-21 23:31:18'),
(69, 'Xiaomi Redmi Note 11S', 10, 6490000, 10, '202211220636samsung-galaxy-a73-5g-xanh-1.jpg', 0, 100, 'Xiaomi Redmi Note 11S 5G một trong những sản phẩm nổi bật của Xiaomi với thiết kế hiện đại, cấu hình vượt trội, hệ thống camera thông minh, dung lượng pin lớn,... Chiếc điện thoại này hứa hẹn sẽ mang lại trải nghiệm tuyệt vời cho người dùng.\r\nBộ 3 camera AI thông minh\r\nĐiện thoại Xiaomi Redmi Note 11S 5G được trang bị camera chính 50 MP giúp bạn ghi lại mọi khía cạnh tươi đẹp trong cuộc sống cho dù tạo dáng trước hoàng hôn hay ở đường phố nhộn nhịp thì chất lượng hình ảnh cho ra rõ nét, độ chi tiết cao, dễ dàng tạo ra những kiệt tác nghệ thuật cho riêng mình. \r\n\r\nẢnh chụp trên Xiaomi Redmi Note 11S 5G\r\n\r\nVới camera góc siêu rộng 8 MP bạn sẽ bắt trọn những khung cảnh thiên nhiên hùng vĩ trong bức ảnh của mình và thích thú khám phá các chi tiết siêu nhỏ trong cuộc sống với camera macro 2 MP.\r\n\r\nẢnh chụp trên Xiaomi Redmi Note 11S 5G\r\n\r\nThiết kế hiện đại, phong cách\r\nĐiện thoại Xiaomi này được thiết phần lưng được bo cong nhẹ và sử dụng chất liệu nhựa giả kính nhám giúp hạn chế bám dấu vân tay. Góp phần tạo nên vẻ bề ngoài cho chiếc điện thoại này trông thời trang và phong cách hơn.\r\n\r\nThiết kế hiện đại, phong cách - Xiaomi Redmi Note 11S 5G\r\n\r\nThân máy với độ mỏng 8.75 mm và khá nhẹ 195 g mang lại cảm giác cầm thoải mái, không mỏi tay, phù hợp cả nam và nữ tay to nhỏ đều cầm vô tư.', 1, 0, '2022-11-21 23:36:23', '2022-11-21 23:36:23'),
(70, 'Xiaomi 12T Pro', 10, 16990000, 10, '202211220642xiaomi-12t-pro-thumb-bac-1-600x600.jpg', 3, 200, 'Cuối cùng thì Xiaomi 12T Pro 5G cũng chính thức lộ diện trên thị trường sau hàng loạt thông tin rò rỉ về thông số, đúng như dự đoán thì phiên bản lần này được nhà sản xuất nâng cấp đáng kể về phần camera, giúp máy trở thành thiết bị có khả năng ghi hình sắc nét thuộc top đầu giới smartphone. Đi kèm với đó là màn hình chất lượng cùng bộ vi xử lý mạnh mẽ xứng tầm flagship.\r\nThiết kế thời thượng\r\nXiaomi 12T Pro sở hữu mặt lưng bo cong ở hai bên và có xu hướng tràn về phía trước, tạo cho máy một cái nhìn mềm mại đầy uyển chuyển khi nhìn từ nhiều góc độ khác nhau, còn về phần thân máy thì 12T Pro được cố định bằng bộ khung làm từ kim loại và nhựa cứng cáp giúp cho điện thoại có khả năng kháng lại những va đập nhẹ trong quá trình sử dụng.\r\n\r\nTạo hình bo cong - Xiaomi 12T Pro\r\n\r\nMặt lưng của 12T Pro được bao phủ bởi một lớp kính mờ có độ nhám nhẹ để cải thiện tình trạng bám dấu vân tay và môi hôi trong quá trình sử dụng, ngoài ra nó còn tăng độ bám tay để bạn có thể cầm nắm thiết bị được chắc chắn.\r\n\r\nKhung viền chắc chắn - Xiaomi 12T Pro\r\n\r\nĐiểm khác biệt mà ta có thể dễ dàng nhận thấy trên Xiaomi 12T Pro so với thế hệ trước nằm ở phần cụm camera, ống kính chính giờ đã được thiết kế dưới dạng ô vuông thay vì hình tròn ta từng thấy trên chiếc Xiaomi 12 Pro trước đây, điều này làm tăng vẻ cuốn hút cho một chiếc điện thoại chụp ảnh, quay phim xịn sò.\r\n\r\nNgắm nhìn đã mắt hơn thông qua màn hình chất lượng\r\nLần này hãng trang bị cho Xiaomi 12T Pro tấm nền AMOLED cao cấp có tỷ lệ tương phản 5.000.000:1, điều này giúp cho nội dung trên màn hình có chiều sâu hơn để mang lại cảm giác chân thực mỗi khi sử dụng. Bên cạnh đó thì 12T Pro còn được hỗ trợ 68 tỷ màu và 100% DCI-P3 để đem lại khả năng tái hiện màu sắc với độ chính xác cực cao.', 1, 0, '2022-11-21 23:42:04', '2022-11-22 00:19:16'),
(71, 'Xiaomi 11T Pro', 10, 10990000, 12, '202211220657Xiaomi-11T-White-1-2-3-600x600.jpg', 0, 99, 'Xiaomi đã trang bị hàng loạt tính năng đỉnh cao cho Xiaomi 11T 5G 128GB như tần số quét màn hình 120 Hz, camera 108 MP cùng vi xử lý Dimensity 1200, chiếc smartphone này chắc chắn sẽ đem đến sự hài lòng dành cho bạn.\r\nẤn tượng trong từng bức ảnh với camera 108 MP\r\nHệ thống 3 camera sau gồm camera chính độ phân giải 108 MP, camera góc rộng 8 MP 120 độ và camera telemacro 5 MP kết hợp cùng phần cứng hiện đại được tích hợp bên trong cho khả năng chụp hình cực kỳ ấn tượng. Việc của bạn là đưa máy lên và nhấn chụp là đã có ngay bức ảnh đẹp với độ sáng vừa đủ, các chi tiết vô cùng sắc nét.\r\n\r\nCụm camera sau ấn tượng - Xiaomi 11T 5G 128GB\r\n\r\nVì hỗ trợ độ phân giải cao nên khả năng thu phóng cũng cực tốt, cho bạn chụp được những đối tượng, khung cảnh ở xa rất rõ ràng.\r\n\r\nKhi chụp ảnh với camera telemacro hệ thống sẽ giúp bạn bắt trọn các vật thể xung quanh với kích thước vi mô vô cùng thú vị. Với mức tiêu điểm dao động từ 3 - 7 cm bạn hoàn toàn có thể cho ra những bức ảnh rõ nét mà không bị mờ nhoè.\r\n\r\nẢnh chụp bằng camera telemacro - Xiaomi 11T 5G 128GB\r\n\r\nCamera góc rộng 120 độ mang đến cho bạn tấm hình toàn cảnh ấn tượng, đẹp mắt. Với camera này bạn sẽ dễ dàng thu lại các bức ảnh về thiên nhiên, cảnh quan hùng vĩ hay chỉ đơn giản là chụp ảnh nhóm đông người.\r\n\r\nKhả năng chụp đêm trên Xiaomi 11T cũng được đánh giá khá cao, ảnh chụp mang tới những tấm ảnh đêm sáng bừng, chất lượng rõ nét với độ chi tiết cao, các vùng sáng tối vô cùng rõ rệt cũng như giảm thiểu độ nhiễu nhất định.\r\n\r\nChế độ chụp ban đêm - Xiaomi 11T 5G 128GB\r\n\r\nKhả năng quay video trên 11T cũng khá ấn tượng, cho phép bạn biến những khoảnh khắc đời thường trở thành những đoạn video, những thước phim điện ảnh vô cùng chuyên nghiệp. Đồng thời, 11T còn hỗ trợ nhiều tính năng như Audio Zoom, Magic Zoom, Time Freeze,... cho bạn dễ bạn chuyển đổi chỉ với một chạm.', 0, 0, '2022-11-21 23:57:24', '2022-11-21 23:57:24'),
(73, 'Xiaomi Redmi Note 10 Pro', 10, 7500000, 5, '202211220723xiaomi-redmi-note-10-pro-thumb-xam-600x600-600x600.jpg', 0, 200, 'Xiaomi tung ra mẫu smartphone Xiaomi Redmi Note 10 Pro, với nhiều ưu điểm nổi bật từ thiết kế cho đến cấu hình, hứa hẹn sẽ tạo ra làn sóng mạnh mẽ trong cộng đồng người hâm mộ công nghệ.\r\nVẻ ngoài sang trọng, thời trang\r\nXiaomi Redmi Note 10 Pro mang vẻ ngoài thời thượng với độ mỏng 8.1 mm và nhẹ 193 g, kết hợp cùng thiết kế bo cong 4 góc mang đến cảm giác cầm nắm thoải mái và dễ chịu.\r\n\r\nXiaomi Redmi Note 10 Pro (8GB/128GB) | Cầm nắm thoải mái\r\n\r\nThiết bị cải tiến với mặt lưng kính cho hiệu ứng bóng bẩy, phản chiếu lại ánh sáng mỗi khi người dùng thay đổi góc nhìn, giúp máy trở nên thu hút và bắt mắt hơn.\r\n\r\nXiaomi Redmi Note 10 Pro (8GB/128GB) | Thiết kế mặt lưng \r\n\r\nBên cạnh đó, cảm biến vân tay được tích hợp ở cạnh bên cho tốc độ nhận diện cao để điện thoại lập tức được mở khóa.\r\n\r\nXiaomi Redmi Note 10 Pro (8GB/128GB) | Cảm biến vân tay được tích hợp ở cạnh bên\r\n\r\nTrải nghiệm giải trí như thật\r\nXiaomi Redmi Note 10 Pro có kích thước màn hình 6.67 inch, với viền bezel khá mỏng mang đến cho người dùng trải nghiệm tinh tế và tuyệt vời nhất.\r\n\r\nXiaomi Redmi Note 10 Pro (8GB/128GB) | Kích thước màn hình 6.67 inch\r\n\r\nMáy trang bị màn hình AMOLED có độ phân giải Full HD+ cho góc nhìn rộng với chất lượng hiển thị tốt, hỗ trợ 100% dải màu DCI-P3 để màu sắc trở nên trung thực và độ sáng cao lên tới 1200 nits cho phép dễ dàng sử dụng ngoài trời nắng gắt.', 1, 0, '2022-11-22 00:23:37', '2022-11-22 00:23:37'),
(74, 'Laptop Apple MacBook Air M1', 14, 33490000, 10, '202211220740macbook-air-m1-2020-gold-600x600.jpg', 32, 200, 'Laptop Apple Air M1 2020 có thiết kế mỏng nhẹ, cao cấp cùng cấu hình mạnh mẽ đến từ con chip M1 độc quyền của Apple hứa hẹn sẽ là một trợ thủ đắc lực của bạn trong công việc, xử lý được tác vụ văn phòng lẫn các thiết kế đồ hoạ.\r\nCấu hình mạnh mẽ, rút ngắn thời gian làm việc\r\nPhiên bản MacBook 2020 này được Apple trang bị cho con chip M1 mang đến hiệu năng vượt trội gấp 3.5 lần thế hệ trước, CPU này có 8 lõi trong đó có 4 lõi hiệu suất cao và 4 lõi tiết kiệm điện mang một hiệu suất đáng kinh ngạc mà lượng điện năng tiêu thụ rất ít. Thao tác các tác vụ văn phòng trong công việc lẫn học tập mượt mà hơn trong các phần mềm Excel, Word, Powerpoint,... xử lý hình ảnh với dung lượng lớn một cách trơn tru, kết xuất 2D hiệu quả.\r\n\r\nĐi cùng chip là Card đồ họa tích hợp 7 nhân GPU nâng cao khả năng xử lý hình ảnh, kết xuất 3D, render video qua các ứng dụng như 3D Max, Premiere, AI, Photoshop,... một cách ổn định với chất lượng cùng độ phân giải cao.\r\n\r\nLaptop RAM 16 GB giúp làm việc đa nhiệm mượt mà, tránh hiện tượng giật lag khi sử dụng nhiều và phải thường xuyên chuyển đổi qua lại. Đáp ứng tốt các thao tác duyệt Web, các ứng dụng văn phòng,...\r\n\r\nMacBook Air M1 2020 7-core GPU - Cấu hình\r\n\r\nỔ cứng SSD 256 GB cho tốc độ khởi động máy, mở ứng dụng nặng nhanh chóng. Bên cạnh đó dung lượng 256 GB cũng đủ dùng với nhu cầu làm việc, học tập của bạn.\r\n\r\nApple MacBook Air M1 2020 (Z124000DE) - SSD\r\n\r\nHệ điều hành macOS Big Sur có giao diện gọn gàng hơn so với các phiên bản trước, về độ nhạy thì ngày càng được cải thiện các thao tác thả, cuộn với Safari diễn ra cực mượt.\r\nSở hữu gam màu xám không gian độc đáo, sang trọng\r\nLớp vỏ của MacBook M1 được làm hoàn toàn từ chất liệu nhôm tái chế vừa bền bỉ vừa góp phần bảo vệ môi trường, các góc cạnh được cắt gọt tỉ mỉ tạo nên sự hài hòa cho toàn bộ sản phẩm. Bên cạnh đó, với khối lượng khá nhẹ, chỉ 1.29 kg, độ mỏng từ 4.1 mm đến 16.1 mm có thể cầm bằng một tay, dễ dàng mang theo chiếc laptop này trong mọi hành trình của bạn sẵn sàng xử lý công việc ở bất kỳ đâu.\r\n\r\nApple MacBook Air M1 2020 (Z124000DE) - Thiết kế\r\n\r\nMagic Keyboard được trang bị trên Mac có hành trình phím 1 mm cho tốc độ gõ nhanh, nhạy chính xác trong từng chi tiết vừa đem lại sự êm ái khi gõ tránh làm phiền mọi người xung quanh. Bàn phím còn được trang bị thêm đèn nền bạn có thể làm việc dễ dàng ngay cả trong môi trường thiếu ánh sáng.\r\n\r\nVới thiết kế mỏng nhẹ như thế việc cắt giảm những cổng kết nối ít sử dụng là một điều tất yếu, trên chiếc máy này Apple chỉ giữ lại 2 cổng Thunderbolt 3 (USB-C) với tính ứng dụng cao. Bạn có thể sạc, truyền dữ liệu, chia sẻ màn hình,... chỉ trong một cổng kết nối.\r\n\r\nNgoài ra, MacBook Air M1 16 GB sử dụng phương thức kết nối không dây là Wi-Fi 6 (802.11ax) và Bluetooth v5.0 cho phép bạn kết nối dễ dàng với các thiết bị ngoại vi, màn hình và máy chiếu với đường truyền ổn định.\r\n\r\nApple MacBook Air M1 2020 (Z124000DE) - Kết nối\r\n\r\nHình ảnh sắc nét từng chi tiết, màu sắc chân thật\r\nMacBook Air M1 sở hữu màn hình 13.3 inch viền siêu mỏng độ phân giải Retina mọi nội dung đều được chiếc màn hình này tái hiện một cách sắc nét, rõ ràng với gam màu vô cùng sống động. \r\n\r\nViền màn hình mỏng kết hợp với tấm nền IPS cho góc nhìn rộng lên đến 178 độ, khiến cho hình ảnh phá bỏ mọi giới hạn về góc nhìn, thoải mái chọn đa dạng góc nhìn mà bạn thích mà không làm ảnh hưởng đến chất lượng hình ảnh, độ sáng 400 nits đảm bảo hình ảnh sẽ không bị nhạt nhòa khi sử dụng laptop ở nơi cường độ sáng cao.\r\n\r\nDải màu rộng P3 kết hợp cùng công nghệ True Tone cho khả năng tái tạo màu sắc hình ảnh một cách chân thực nhất, tự động tuỳ chỉnh màu sắc và ánh sáng để đáp ứng tốt nhu cầu giải trí và làm việc liên quan đến đồ họa.', 1, 0, '2022-11-22 00:40:14', '2022-12-26 19:41:19'),
(75, 'MacBook Pro 16 M1 Max', 14, 92000000, 20, '202211220747macbook-air-m1-2020-gold-600x600.jpg', 17, 100, 'Công nghệ mới cho thế hệ sản phẩm tân tiến, mang đến những trải nghiệm vượt bậc hơn cùng laptop Apple MacBook Pro 16 M1 Max 2021 với bộ vi xử lý mới nhất, hiện đại cùng thiết kế đẳng cấp, thời thượng, xứng tầm người cộng sự đa năng trong mọi lĩnh vực kể cả giải trí hay công việc.\r\nBước đột phá trong công nghệ cùng chip M1 Max\r\nĐược nâng cấp và cải tiến từ thế hệ chip M1, bộ vi xử lý Apple M1 Max mang đến tốc độ xử lý mọi việc đáng kinh ngạc, nhanh hơn đến 70% so với thế hệ tiền nhiệm. Không những thế, hiệu suất của card đồ họa GPU 32 lõi lớn cho những tác vụ đồ họa nhanh hơn gấp 4 lần so với chip M1. Mặc dù đắc lực là thế nhưng con chip này lại không gây hao tốn quá nhiều điện năng, đa năng nhưng tiết kiệm tối ưu.\r\n\r\nMacBook Pro 16 inch 2021 sở hữu Apple M1 Max được sản xuất trên tiến trình 5 nm sở hữu CPU 10 lõi tối tân với băng thông bộ nhớ lên đến 400 GB/s cùng 57 tỷ bóng dẫn, mang đến hiệu năng mạnh mẽ, nhanh hơn gấp 2 lần so với thế hệ chip Core i9 trên thế hệ tiền nhiệm, hỗ trợ bạn xử lý mọi tác vụ văn phòng đơn giản hay khó nhằn với Word, Excel,...  một cách trơn tru hay thoả mãn sự sáng tạo của bạn với chất lượng hình ảnh tuyệt hảo.\r\n\r\nSức mạnh được nâng tầm đáng kể khi được thiết kế kèm theo card đồ họa tích hợp 32 core GPU, khơi nguồn cảm hứng trong bạn cùng khả năng thao tác mượt mà những tác vụ như render video, kết xuất 3D hay chỉnh sửa những hình ảnh phức tạp một cách chuyên nghiệp cùng các ứng dụng nhà Adobe, thậm chí bạn còn có thể phát trực tiếp với độ nét cao đến 8K, nâng cao đáng kể hiệu suất công việc.\r\n\r\nTăng phần mạnh mẽ cùng RAM 32 GB mang đến cho bạn một phiên bản laptop đa nhiệm hơn bao giờ hết, bạn có thể mở nhiều ứng dụng, phần mềm cùng lúc như vừa chơi game vừa nghe nhạc hay vừa thiết kế đồ họa cùng hàng chục layer mà vẫn mượt mà, không lo xảy ra tình trạng giật lag không mong muốn.\r\n\r\nMacBook Pro 16 M1 Max 2021/32 core-GPU - Cấu hình\r\n\r\nKhông dừng lại ở đó, khi chiếc MacBook Pro 16 inch này được trang bị 1 TB SSD cung cấp không gian khổng lồ cho mọi nhu cầu lưu trữ dữ liệu của bạn, cùng khả năng phản hồi nhanh chóng chỉ khi vừa mở máy, rút ngắn thời gian làm việc, nâng cao năng suất của người dùng.\r\n\r\nMacBook Pro 16 M1 Max 2021/32 core-GPU - SSD\r\n\r\nĐặc biệt hơn khi hệ điều hành Mac OS sở hữu giao diện bắt mắt, thu hút người dùng cùng những tính năng hữu ích hỗ trợ tối ưu hơn cho mọi nhu cầu của bạn cả trong giải trí hay công việc hằng ngày.\r\n\r\nMacBook Pro 16 M1 Max 2021/32 core-GPU - macOS\r\n\r\nĐẳng cấp toát lên từ thiết kế hiện đại, thời thượng\r\nMacbook đã cho ra đời một thế laptop vượt trội với thiết kế có phần vuông vức hơn các thế hệ tiền nhiệm cùng độ mỏng nhẹ hoàn mỹ khi chỉ nặng 2.2 kg và dày 16.8 mm, được chế tác từ nhôm tái chế cho khả năng bền bỉ cao, cho một phiên bản vừa di động vừa bảo vệ môi trường, sẵn sàng nằm gọn trong balo cùng bạn đi đến bất kỳ đâu.', 1, 0, '2022-11-22 00:47:13', '2022-12-26 09:54:04'),
(76, 'MacBook Air M2 2022', 14, 38990000, 10, '202211220752apple-macbook-air-m2-2022-16gb-256gb-thumb-600x600.jpg', 1, 100, 'Nếu bạn là một tín đồ của nhà Táo thì không thể bỏ qua siêu phẩm MacBook Air M2 2022 được trình làng vào giữa năm 2022 này. Ngoại hình quyến rũ, thời thượng, màn hình Retina siêu sắc nét cùng con chip Apple M2 mạnh mẽ sẽ là sự lựa chọn hàng đầu cho người dùng.\r\nCân mọi tác vụ từ cơ bản đến nâng cao với chip Apple M2\r\nMacBook năm nay được trang bị dòng chip Apple M2 sản xuất với tiến trình 5 nm, tốc độ băng thông 100GB/s và hơn 20 nghìn tỷ bóng bán dẫn, nhiều hơn 25% cùng với hiệu suất được nâng cao hơn 18% so với dòng chip M1, từ đó giải quyết nhẹ nhàng mọi tác vụ học tập, làm việc của người dùng từ cơ bản đến nâng cao mà không lo bị gián đoạn giữa chừng.\r\n\r\n8 nhân GPU được tích hợp trên máy giúp người dùng vận hành tốt các tác vụ liên quan đến đồ họa như chỉnh sửa ảnh, render video, phát video 4K, 8K ProRes 12,... trên các ứng dụng đồ họa của nhà Adobe như Illustrator, Premiere, Photoshop,... một cách chuyên nghiệp với khả năng xử lý lên đến 15.8 nghìn tỷ tác vụ chỉ trong một giây.\r\n\r\nBộ nhớ RAM 16 GB cho khả năng đa nhiệm được nâng cao đáng kể khi cho phép người dùng mở nhiều tab Safari cùng lúc hay vừa chỉnh sửa ảnh vừa xem phim trên Netflix mà không xảy ra hiện tượng giật lag. Không gian lưu trữ đủ để bạn tải các tệp tài liệu về máy để học tập hay làm việc cơ bản nhờ ổ cứng 256 GB.\r\n\r\nMacBook Air M2 2022 10-core GPU - Cấu hình\r\n\r\nXứng danh là doanh nghiệp hàng đầu trong hệ thống bảo mật nhờ hệ điều hành MacOS cùng vi xử lý Apple Silicon, vừa mang đến một kho ứng dụng đa dạng vừa nâng cao khả năng bảo mật vượt trội, giúp người dùng quản lý chặt chẽ hơn về quyền riêng tư của mình.\r\n\r\nChinh phục mọi ánh nhìn với diện mạo mới đầy cuốn hút\r\nMột sự thay đổi ngoạn mục trên chiếc MacBook Air năm nay là đã thay đổi từ thiết kế thuôn gọn thành bề dày ngang bằng nhau giống như các mẫu MacBook Pro, bao bọc xung quanh máy là lớp vỏ kim loại cứng cáp cùng logo trái táo cắn dở đặc trưng mang đến một vẻ đẹp hoàn mỹ mà ai ai cũng phải ngước nhìn. Cân nặng 1.24 kg cho độ di động cực cao, sẵn sàng đồng hành cùng bạn mọi lúc mọi nơi.\r\n\r\nMacBook Air M2 2022 10-core GPU - Thiết kế\r\n\r\nTrải nghiệm gõ phím êm tay, yên tĩnh nhờ bàn phím Magic Key có hành trình phím 1 mm, hạn chế tối đa việc ảnh hưởng tiếng ồn đến những người xung quanh. Tuy được thiết kế tương tự như MacBook Pro nhưng bàn phím của MacBook Air đã thay thế thanh Touch Bar thành dãy phím F chức năng, mang đến sự thuận tiện hơn cho các thao tác nhập liệu. Laptop còn hỗ trợ đèn bàn phím giúp bạn làm việc tốt hơn trong điều kiện môi trường thiếu sáng.', 1, 0, '2022-11-22 00:52:47', '2022-12-13 09:52:52'),
(77, 'Acer Nitro 5 Gaming AN515 45 R6EV R5', 15, 19090000, 20, '202211220759acer-nitro-5-gaming-an515-45-r6ev-r5-5600h-8gb-600x600.jpg', 7, 200, 'Laptop Acer Nitro 5 Gaming AN515 45 R6EV R5 (NH.QBMSV.006) gây ấn tượng mạnh mẽ với thiết kế cá tính cùng bộ cấu hình lý tưởng đến từ con chip AMD và card đồ họa rời dòng GTX, hứa hẹn sẽ là một người bạn đồng hành hoàn hảo dành cho các game thủ hay giới sáng tạo nội dung.\r\nVẻ ngoài độc đáo, mạnh mẽ\r\nLaptop Acer Nitro 5 Gaming được bao bọc bởi lớp vỏ nhựa cứng cáp, bền bỉ, các đường góc cạnh cắt vát tỉ mỉ, tinh xảo, nắp lưng có khắc logo Acer đặt tinh tế giữa 2 tia sét, máy sở hữu khối lượng 2.2 kg và bề dày 23.9 mm, vẫn thuận tiện bỏ vừa vào balo, sẵn sàng đồng hành mọi lúc mọi nơi cùng bạn trên mọi mật trận game ảo cùng bạn bè hay mang đi học, đi làm mà không lo quá cồng kềnh, vướng víu.\r\n\r\nAcer Nitro 5 Gaming AN515 45 R6EV R5 5600H (NH.QBMSV.006) - Thiết kế\r\n\r\nBàn phím có kích thước hợp lý, rộng rãi, hành trình phím sâu, nút bấm êm, độ nảy tốt và tích hợp vùng phím số bên phải giúp thao tác lệnh nhanh nhạy, chuẩn xác. Bên cạnh đó còn trang bị đèn nền chuyển màu RGB cá tính làm tăng thêm cảm hứng cho game thủ cũng như sự tiện dụng ngay cả ở nơi thiếu sáng.\r\n\r\nAcer Nitro 5 Gaming AN515 45 R6EV R5 5600H (NH.QBMSV.006) - Bàn phím\r\n\r\nLaptop Acer trang bị dãy cổng kết nối thông dụng như: 3 x USB 3.2, USB Type-C, HDMI, LAN (RJ45), Jack tai nghe 3.5 mm liên kết dễ dàng, truyền tải thông tin nhanh chóng đến các thiết bị ngoại vi tương thích mà không cần đến adapter chuyển đổi rườm rà.\r\n\r\nAcer Nitro 5 Gaming AN515 45 R6EV R5 5600H (NH.QBMSV.006) - Cổng kết nối\r\n\r\nĐồng thời, laptop hỗ trợ hai chuẩn kết nối không dây Bluetooth 5.0 nâng tốc độ này lên 50 Mbps để người dùng sử dụng thoải mái hơn và Wi-Fi 6 (802.11ax) cho tốc độ truyền tải dữ liệu nhanh hơn lên đến hơn 10.000 Mbps, thời gian hồi đáp dữ liệu giữa các thiết bị gần như ngay tức thời.\r\n\r\nCấu hình vượt trội, đa tác vụ\r\nLaptop AMD Ryzen 5 5600H mang cấu trúc 6 nhân 12 luồng đem đến tốc độ xung nhịp trung bình đạt 3.30 GHz và tối đa lên đến Turbo Boost 4.2 GHz kết hợp cùng bộ nhớ đệm 16 MB, cung cấp một hiệu năng mạnh mẽ giúp bạn giải quyết trơn tru, đa tác vụ, không chỉ chiến game êm mượt mà còn có thể chạy tốt các phần mềm văn phòng nặng hay thiết kế đồ họa hoàn hảo.\r\n\r\nBộ nhớ RAM 8 GB chuẩn DDR4 2 khe (1 khe 8 GB + 1 khe rời) tốc độ Bus RAM 3200 MHz cho bạn khả năng vừa làm việc vừa giải trí hoàn hảo, đa nhiệm mượt mà, hạn chế tình trạng giật lag, đơ đứng màn hình. Bên cạnh đó còn có thể nâng cấp đến 32 GB đáp ứng tối ưu nhu cầu sử dụng của bạn.', 1, 0, '2022-11-22 00:59:07', '2022-12-23 08:19:26'),
(78, 'Acer Aspire 7 Gaming A715 42G R4XX R5', 15, 14590000, 10, '202211220802acer-aspire-7-gaming-a715-42g-r4xx-r5-5500u-8gb-600x600.jpg', 13, 100, 'Laptop Acer Aspire 7 Gaming A715 42G R4XX R5 (NH.QAYSV.008) có cấu hình mạnh nhờ card đồ họa rời NVIDIA GeForce GTX 1650, sẽ là một lựa chọn tốt cho nhu cầu làm việc, đồ hoạ và giải trí.\r\n• Laptop gaming có sức mạnh hiệu năng vượt trội nhờ trang bị chip ADM Ryzen 5 5500U và card rời GeForce GTX 1650 4 GB giúp máy chạy ổn định các tác vụ nặng như thiết kế 2D chuyên nghiệp, render video,… cân tốt các tựa game hot như LOL, CS:GO, GTA V,...\r\n\r\n• RAM 8 GB hỗ trợ nâng cấp đến 32 GB cùng SSD 256 GB cho khả năng đa nhiệm mượt mà, nâng cao tốc độ phản hồi cho máy.\r\n\r\n• Laptop Acer Aspire thiết kế đơn giản với lớp vỏ nhựa màu đen hiện đại, với khối lượng máy 2.1 kg và độ dày 22.9 mm không quá cồng kềnh để bạn có thể mang theo.\r\n\r\n• Tiện lợi hơn nhờ tích hợp bản lề 180 độ dễ dàng chia sẻ màn hình với mọi người và đèn nền đơn sắc giúp thao tác chuẩn xác cả khi dùng trong không gian tối.\r\n\r\n• Laptop Acer hỗ trợ đầy đủ các kết nối thông dụng gồm cổng USB 3.2, HDMI, LAN (RJ45), USB 2.0, USB Type-C đáp ứng tốt mọi nhu cầu kết nối trong công việc.\r\n\r\n• Trải nghiệm góc nhìn rộng trên tấm nền IPS trên màn hình 15.6 inch cùng khả năng chống chói Anti Glare và công nghệ Acer ComfyView lọc ánh sáng xanh, bảo vệ tốt cho thị giác của bạn.\r\n\r\n• Công nghệ Acer TrueHarmony mang đến chất âm chân thực, trong trẻo và mạnh mẽ, cho trải nghiệm giải trí tuyệt vời ngay trên laptop của bạn.', 1, 0, '2022-11-22 01:02:21', '2022-12-25 22:44:03'),
(79, 'Acer Nitro 5 Tiger AN515 58 52SP', 15, 27590000, 10, '202211220806acer-nitro-5-tiger-043416-600x600.jpg', 1, 200, 'Laptop Acer Nitro 5 Tiger AN515 58 52SP i5 (NH.QFHSV.001) mang đến mọi người một chiếc laptop mới đúng nghĩa về cả thiết kế lẫn cấu hình bên trong, đồng hành cùng bạn trong mọi cuộc chiến với hiệu năng mạnh mẽ vượt trội.\r\nCấu hình vượt trội với CPU Intel Gen 12 mới nhất\r\nLaptop Acer Nitro được ưu ái trang bị bộ vi xử lý Intel thế hệ 12 Alder Lake với nhiều cải tiến vượt bậc. Để có cái nhìn tổng quan hơn về sức mạnh của chip Intel Core i5 12500H, chúng ta hãy cùng nhìn các thông số từ Cinebench R20 đo được nhé.\r\n\r\nAcer Nitro 5 Tiger AN515 58 52SP i5 12500H (NH.QFHSV.001) - hiệu năng\r\n\r\nIntel Core i5 12500H đem đến số điểm rất cao đảm bảo hiệu vượt trội trong nhiều tác vụ, trong đó bao gồm điểm đa nhân lên đến 4723 điểm và đơn nhân 642 điểm. Điều này chứng tỏ chúng ta sẽ có thể xử lý được hầu hết các tác vụ nặng hiện nay. Vì có mức hiệu năng cao nên mức điện của CPU sẽ đạt con số khá khủng 87 W và lên đến 93 độ nếu không bật Nitro Sense.\r\n\r\nAcer Nitro 5 Tiger AN515 58 52SP i5 12500H (NH.QFHSV.001) - Cinebench R23\r\n\r\nMức nhiệt cao còn chứng minh một điều về độ hiệu quả của Nitro Sense. Nitro Sense là một ứng dụng điều chỉnh tốc độ quạt và các chế độ sử dụng được tích hợp sẵn trên dòng laptop Nitro. Tại chiếc laptop này, đã có bước tiến so với hệ cũ khi có thể điều khiển tốc độ quạt lên đến 7000 vòng trên phút (thế hệ cũ khoảng 5000 - 6000 vòng). Việc này giúp cải thiện rất nhiều khả năng tản nhiệt trong các tựa game đồ hoạ đỉnh cao hiện nay.\r\n\r\nAcer Nitro 5 Tiger AN515 58 52SP i5 12500H (NH.QFHSV.001) - Nitro Sense\r\n\r\nLaptop Acer Nitro 5 Tiger chọn GPU rời NVIDIA GeForce RTX 3050 4GB làm đối tác với CPU để có thể tạo nên một cặp đôi cùng tiến với nhiều tác vụ từ đồ hoạ cơ bản (thiết kế hình ảnh, dựng video,...) cho đến chiến những game đòi hỏi cấu hình tương đối cao trên thị trường hiện nay. Để xác thực những điều này, chúng ta cùng nhìn thông số thực tế khi mình trải nghiệm game nhé.\r\n\r\nAcer Nitro 5 Tiger AN515 58 52SP i5 12500H (NH.QFHSV.001) - hiệu năng 1\r\n\r\nTựa game bắn súng FPS đình đám CS:GO sẽ được chọn đầu tiên. Mình thiết lập mức cấu hình cao và bật tối đa tốc độ quạt ở Nitro Sense, mức nhiệt cho ra lần lượt với con số không tưởng là 54 độ CPU và 45 độ GPU. Ngạc nhiên hơn nữa với mức nhiệt \"khiêm tốn\" như vậy mà máy có thể cho ra 145 FPS khi đi cùng tần số quét 144 Hz, con số này giúp mình có thể trải nghiệm chơi game một cách mượt mà, không bao giờ phải gặp những tình huống giật lag làm ảnh hưởng đến kết quả trận chiến.', 0, 0, '2022-11-22 01:06:15', '2022-12-13 09:53:32'),
(80, 'Acer Aspire 3 A315 58 787C i7', 15, 19490000, 20, '202211220835acer-aspire-3-a315-58-787c-i7-nxaddsv00j-thumb-600x600.jpg', 4, 121, 'Laptop Acer Aspire 3 A315-58-55F3 i5 (NX.ADDSV.00J) có thiết kế đơn giản, thanh lịch cùng cấu hình mạnh mẽ với bộ vi xử lý Intel Core i7 thế hệ 11 hiện đại, hứa hẹn sẽ là chiếc laptop học tập - văn phòng phù hợp cho sinh viên, học sinh và nhân viên văn phòng.\r\n• Laptop Acer Aspire được trang bị màn hình kích thước lớn 15.6 inch cho không gian rộng lớn, độ phân giải Full HD (1920 x 1080) hiển thị rõ nét hình ảnh. Kết hợp cùng công nghệ màn hình Acer ComfyView, LED Backlit và TFT để tăng trải nghiệm xem của người dùng.\r\n\r\n• Bộ vi xử lý Intel Core i7 Tiger Lake 1165G7 trên laptop Acer có thể xử lý một cách nhanh chóng mọi tác vụ từ nhẹ đến nặng. Card Intel Iris Xe Graphics hỗ trợ cho các công việc chỉnh ảnh hay thiết kế cơ bản trên Photoshop, Canva,...\r\n\r\n• RAM 8 GB chuẩn DDR4 chạy mượt các ứng dụng, 512 GB SSD chuẩn NVMe PCIe cho phép laptop khởi động ứng dụng nhanh chóng hơn.\r\n\r\n• Cổng kết nối đa dạng: USB 2.0, USB 3.2, HDMI, Jack tai nghe 3.5 mm và LAN (RJ45) cho phép liên kết các thiết bị ngoại vi một cách dễ dàng nhất.\r\n\r\n• Thiết kế đơn giản, hiện đại với lớp vỏ nhựa màu bạc cùng khối lượng 1.7 kg cùng bạn đến khắp mọi nơi.', 0, 0, '2022-11-22 01:35:03', '2022-12-25 19:56:51'),
(81, 'Asus TUF Gaming FX506LHB i5', 17, 18790000, 6, '202211220838asus-tuf-gaming-fx506lhb-i5-hn188w-600x600.jpeg', 1, 200, 'Laptop Asus TUF Gaming FX506LHB i5 (HN188W) với cấu hình ổn định của chip Intel thế hệ 10 và diện mạo chuẩn laptop gaming hầm hố sẵn sàng cùng bạn chinh phục mọi đấu trường ảo.\r\n• Laptop Asus mang trong mình CPU Intel Core i5 10300H mạnh mẽ cùng card màn hình rời GTX 1650 4 GB đảm bảo các khả năng chiến game tốt các tựa game đình đám: GTA V, FIFA Online 4,... hay chỉnh sửa thiết kế ảnh Adobe trơn tru.\r\n\r\n• Với RAM 8 GB, laptop cung cấp khả năng đa nhiệm tốt các tab công việc nhẹ nhàng, để có trải nghiệm tốt khuyến khích nên nâng cấp RAM để có trải nghiệm tốt nhất. SSD 512 GB cho không gian lưu trữ đủ dùng, tăng tốc độ truy xuất dữ liệu.\r\n\r\n• Màn hình 15.6 inch tích hợp trên máy với tần số quét 144 Hz mang lại trải nghiệm chiến game đỉnh cao, ổn định không giật lag hình.\r\n\r\n• Đầy đủ cổng giao tiếp sẵn sàng kết nối với các thiết bị ngoại vi: USB Type-C, USB 3.2, HDMI, LAN (RJ45),...\r\n\r\n• Đèn bàn phím chuyển màu RGB được trang bị trên laptop Asus TUF Gaming cho phép người dùng điều chỉnh màu theo sở thích.\r\n\r\n• Công nghệ DTS X:Ultra Audio cho chất âm tốt, mang lại không gian chơi game phấn khích và chân thực hơn.', 1, 0, '2022-11-22 01:38:34', '2022-12-13 09:53:02'),
(82, 'Asus VivoBook 15X OLED A1503ZA i5', 17, 19690000, 10, '202211220842asus-vivobook-15x-oled-a1503za-i5-l1290w-040722-050332-600x600.jpg', 3, 100, 'Laptop Asus VivoBook 15X OLED A1503ZA i5 (L1290W) không chỉ giúp mình giải quyết triệt để các tác vụ học tập, văn phòng nhờ sức mạnh bùng nổ đến từ chip Intel Gen 12 dòng H mà còn mang đến không gian giải trí trọn vẹn khi sở hữu những thông số màn hình ấn tượng. \r\nBùng nổ sức mạnh với vi xử lý Intel Gen 12\r\nLaptop Asus VivoBook mang trên mình con chip Intel Core i5 Alder Lake 12500H mạnh mẽ với 12 nhân 16 luồng giúp bạn giải quyết mượt mà mọi tác vụ học tập, văn phòng trên Word, Excel, PowerPoint,... Card đồ họa tích hợp Intel UHD Graphics xuất ảnh chất lượng 2D rõ nét, có thể thực hiện các thao tác chỉnh sửa hình ảnh đơn giản trên Photoshop, Canva, Lightroom,...   \r\n\r\nMình trải nghiệm VivoBook 15X như một người dùng là học sinh - sinh viên, đầu tiên mình sử dụng chiếc laptop để học tập trực tuyến, mở song song giữa Microsoft Team để tham gia lớp học, coi slide bài giảng trên PowerPoint và mở một vài cửa sổ Chrome để tra cứu thông tin. Với những tác vụ kể trên máy chạy rất mượt mà, mình liên tục chuyển qua lại giữa tác tác vụ mà không gặp bất kỳ hiện tượng giật lag.\r\n\r\nAsus VivoBook 15X OLED A1503ZA i5 12500H (L1290W) - Đa tác vụ\r\n\r\nSau khi học xong, mình dành ra một khoảng thời gian để giải trí với tựa game Liên Minh Huyền Thoại. Hiệu năng mạnh mẽ từ chip Intel Gen 12 cân game dễ dàng, mức FPS giao động từ 80 - 100, không cao nhưng cũng dễ hiểu bởi máy chỉ được trang bị card tích hợp Intel UHD Graphics. Mình còn tận dụng sức mạnh của bộ vi xử lý Intel Gen 12 dòng H mạnh mẽ để chỉnh sửa hình ảnh và render video. Mọi thứ đều hoạt động một cách trơn tru, máy không bị giật và đứng màn hình. \r\n\r\nAsus VivoBook 15X OLED A1503ZA i5 12500H (L1290W) - LOL\r\n\r\nLaptop Asus được tích hợp một thanh RAM 8 GB nhưng để nâng cao hơn nữa trải nghiệm, mình khuyên bạn nên nâng thêm RAM cho chiếc laptop. Khi nâng cấp thêm RAM, VivoBook 15X không chỉ có nhiều không gian lưu trữ tạm thời các ứng dụng mà còn kích hoạt được Dual-Channel, bạn sẽ “mở khóa” luôn được Intel Xe Graphics vốn chỉ hoạt động trên RAM kênh đôi. Với Intel Xe Graphics các tác vụ liên quan đến đồ họa và kể cả trải nghiệm game sẽ được cải thiện, FPS trong mỗi trận đấu cũng sẽ tăng cao.', 1, 0, '2022-11-22 01:42:44', '2022-12-14 07:19:54'),
(83, 'Asus TUF Gaming FX517ZC i5', 17, 26790000, 10, '202211220845asus-tuf-gaming-fx517zc-i5-hn077w-190322-114627-600x600.jpg', 1, 100, 'Nổi bật ở vẻ ngoài ấn tượng cùng laptop CPU Intel Gen 12 cho cấu hình vượt trội, laptop Asus TUF Gaming FX517ZC (HN077W) sẵn sàng cùng bạn chinh phục các tựa game đình đám hiện nay.\r\n• Laptop Asus được trang bị CPU Intel Core i5 Alder Lake 12450H mạnh mẽ tiết kiệm điện năng cùng card màn hình rời NVIDIA RTX 3050 4 GB chạy ổn định trơn tru các tựa game nổi tiếng: GTA V, PUBG, CS:GO,... với mức cài đặt cao hay thiết kế đồ hoạ với Photoshop, After Effect, Ai,... cũng không làm khó được chiếc máy.\r\n\r\n• Chiếc máy được trang bị RAM 8 GB cho khả năng chơi game, đa nhiệm tốt kết hợp với bộ nhớ trong SSD 512 GB cho không gian lưu trữ đủ dùng, tăng tốc độ truy xuất dữ liệu và khởi động laptop cực nhanh.\r\n\r\n• Màn hình 15.6 inch có độ phân giải Full HD (1920 x 1080) sắc nét. Tần số quét 144 Hz, công nghệ Adaptive Sync giúp mượt mà trong từng khung hình chiến game. Công nghệ chống chói Anti Glare giúp loại bỏ hiện tượng bóng gương, tấm nền IPS tích hợp giúp góc nhìn người dùng rộng lên đến 178 độ.\r\n\r\n• Dòng laptop Asus TUF Gaming với thiết kế ấn tượng với gam màu đen chủ đạo được hoàn thiện tinh tế từ lớp vỏ nhựa cứng cáp. Chiếc máy có khối lượng 2 kg, độ mỏng 20.7 mm dễ dàng nằm gọn gàng trong chiếc balo mang theo bên người. \r\n\r\n• Đa dạng cổng giao tiếp dễ dàng kết nối với thiết bị ngoại vi gồm USB 3.2 Gen 2 Type-C, 2 x USB 3.2, HDMI, LAN (RJ45), Thunderbolt 4. Đèn nền bàn phím đơn sắc tích hợp giúp người dùng dễ dàng định vị vị trí từng phím trong môi trường thiếu sáng.\r\n\r\n• Công nghệ âm vòm Dolby Atmos cao cấp giúp tái tạo không gian âm thanh sống động, đạt chuẩn Hi-Res mang lại không gian giải trí nghe nhạc, xem phim, chơi game thư giãn hơn bao giờ hết ngay trên chiếc laptop gaming này.', 0, 0, '2022-11-22 01:45:48', '2022-12-25 21:45:56'),
(84, 'Dell Gaming G15 5515 R5', 18, 23090000, 11, '202211220849dell-gaming-g15-5515-r5-p105f004dgr-291121-114930-600x600.jpg', 4, 99, 'Mang đến sức mạnh của chiến binh, laptop Dell Gaming G15 5515 R5 5600H (P105F004DGR) không những sở hữu thiết kế góc cạnh đầy mạnh mẽ, mà còn được trang bị cấu hình vượt trội, luôn trong tư thế sẵn sàng chiến đấu ở mọi đấu trường.\r\nĐánh bật mọi đối thủ trên chiến trường ảo\r\nLaptop AMD Ryzen 5 5600H sở hữu 6 nhân 12 luồng, đem đến tốc độ cơ bản 3.30 GHz và đạt tối đa lên đến 4.2 GHz nhờ Turbo Boost, cùng bộ nhớ đệm 16 MB hỗ trợ bạn xử lý mọi loại tác vụ từ cơ bản đến nâng cao một cách trơn tru, hoàn thành nhanh gọn những công việc văn phòng cùng Word, Excel,... hay trải nghiệm những tựa game đầy kịch tính hiện nay.\r\n\r\nLaptop RAM 16 GB chuẩn DDR4 2 khe (1 khe 8 GB + 1 khe 8 GB) với tốc độ Bus RAM 3200 MHz cũng khả nâng cấp RAM tối đa đến 32 GB, thỏa mãn mọi nhu cầu của người dùng trong cả công việc hay giải trí khi sở hữu khả năng mở cùng lúc nhiều ứng dụng, hàng chục layer hay hàng loạt các tab và chuyển đổi qua lại mượt mà, không lo xảy ra tình trạng giật, lag máy.\r\n\r\nDell Gaming G15 5515 R5 5600H (P105F004DGR) - Cấu hình\r\n\r\nĐánh thức đam mê trong bạn khi được trang bị card đồ họa rời NVIDIA GeForce RTX3050, 4 GB kèm theo, mang đến những hình ảnh rõ nét, chân thật đến từng chi tiết, vừa cho phép bạn chỉnh sửa hình ảnh, render video một cách chuyên nghiệp cùng Photoshop, Ai, Lightroom,... vừa hỗ trợ chiến những trận game trong PUBG: Battlegrounds, LOL,... đầy hấp dẫn với ưu thế vượt trội.\r\n\r\nDell Gaming G15 5515 R5 5600H (P105F004DGR) - Card đồ hoạ\r\n\r\nThỏa sức lưu trữ mọi loại dữ liệu mà bạn yêu thích trên ổ cứng SSD 512 GB NVMe PCle (Có thể tháo ra, lắp thanh khác tối đa 2 TB (2280) / 1 TB (2230)) với không gian rộng rãi, đồng thời hỗ trợ bạn mở máy và vận hành các ứng dụng trong thời gian nhanh chóng chỉ vài giây, nâng cao hiệu quả công việc.\r\n\r\nDell Gaming G15 5515 R5 5600H (P105F004DGR) - SSD\r\n\r\nTrải nghiệm mọi ứng dụng của nhà Office nhờ hệ điều hành Windows 11 Home SL và Office Home & Student 2021 vĩnh viễn đã được tích hợp sẵn, cho phép bạn tận hưởng trọn vẹn mọi nguồn tài nguyên có trong máy.', 1, 0, '2022-11-22 01:49:57', '2022-12-13 09:53:08');
